definition.script:
node {
    sh '''
        echo before...;pwd;ls -l
    '''
    deleteDir()
    sleep(time: 5, unit: 'SECONDS')
    sh '''
        echo after...;pwd;ls -l
    '''
    dir('mci'){
        checkout scmGit(
            branches: [[name: 'main']],
            userRemoteConfigs: [[url: 'https://github.com/iamrealbuddy/mci.git/']])
    }
    sleep(time: 5, unit: 'SECONDS')
    sh '''
        pwd;ls -l
    '''
}

pipeline {
    agent { 
        dockerfile {
            filename 'mci/docker/df-agent-debian-py'
        } 
    }
    stages {
        stage('build') {
            steps {
                echo "Building...debian-agent"
                sh '''
                cat /etc/*-release
                '''
            }
        }
        stage('test') {
            steps {
                echo "Testing...debian-agent"
                sh '''
                pwd
                ls -l
                chmod +x mci/scripts/hi.sh
                ./mci/scripts/hi.sh
                python3 -V
                '''
            }
        }
    }
}
